function myMap(){geocoder=new google.maps.Geocoder;var mapProp={center:new google.maps.LatLng(-23.554172,-46.6644255),zoom:12,scrollwheel:!0,draggable:!0},map=new google.maps.Map(document.getElementById("googleMap"),mapProp);geocoder.geocode({address:"inovaBra habitat"},function(results,status){if("OK"==status){map.setCenter(results[0].geometry.location);new google.maps.Marker({map:map,position:results[0].geometry.location})}else alert("Geocode was not successful for the following reason: "+status)})}